// Create a cell factory for the "Actions" column
@Override
public TableCell<Cinema, Void> call(TableColumn<Cinema, Void> param) {
    return new TableCell<Cinema, Void>() {
        @Override
        protected void updateItem(Void item, boolean empty) {
            super.updateItem(item, empty);

            // If the row is not empty, get the cinema associated with it
            if (!empty) {
                Cinema cinema = getTableView().getItems().get(getIndex());

                // Create a button to open a new window to display the movies of the cinema
                Button showMoviesButton = new Button("Show Movies");

                // Set the on action for the button to open the new window
                showMoviesButton.setOnAction(event -> {
                    try {
                        FXMLLoader loader = new FXMLLoader();
                        Parent root = (Parent) loader.load(getClass().getResource("/fxml/MovieList.fxml").openStream());
                        MovieListController movieListController = (MovieListController) loader.getController();
                        movieListController.setCinema(cinema);

                        // Create a new stage and set the scene for the stage to the root node of the fxml file
                        Stage stage = new Stage();
                        stage.setScene(new Scene(root));

                        // Set the title of the window
                        stage.setTitle("Movie List");

                        // Show the stage and wait for it to be closed before returning
                        stage.showAndWait();
                    } catch (IOException e) {
                        e.printStackTrace();
                    }
                });

                setGraphic(showMoviesButton);
            }
        }
    };
}

@Override

protected void updateItem(Void item, boolean empty) {

    super.updateItem(item, empty);

    if (empty) {

        setGraphic(null);

    } else {

        // Rcuprer le cinma associ  cette ligne

        Cinema cinema = getTableView().getItems().get(getIndex());

        if (cinema.getStatut().equals("Accepted")) {

            // Afficher le bouton "Show Movies" si le statut est "Accepted"

            setGraphic(showMoviesButton);

        } else {

            // Afficher les boutons "Accepter" et "Refuser" si le statut est "En attente"

            setGraphic(new HBox(acceptButton, refuseButton));

        }

    }

}

