import javafx.collections.FXCollections;
import javafx.collections.ObservableList;

public class Main {
    private void start() {
        ObservableList<Cinema> cinemas = FXCollections.observableArrayList(
                new Cinema("Le cinema 1", "Paris", "Pending"),
                new Cinema("Le cinema 2", "Londres", "Accepted"),
                new Cinema("Le cinema 3", "New York", "Pending")
        );

        // Searching for cinemas with "Par" in their name
        filterCinemas(cinemas, "Par");
    }

    private void filterCinemas(ObservableList<Cinema> cinemas, String searchText) {
        ObservableList<Cinema> filteredList = FXCollections.observableArrayList();

        // Iterate through each cinema in the list and check if it contains the specified text
        for (Cinema cinema : cinemas) {
            if (cinema.getNom().toLowerCase().contains(searchText.toLowerCase())) {
                filteredList.add(cinema);
            }
        }

        // Update the TableView with the list of filtered cinemas
        listCinema.setItems(filteredList);
    }
}

