public class Sample {
    @FXML
    private AnchorPane FilterAnchor;

    @FXML
    private ObservableList<CheckBox> addressCheckBoxes = FXCollections.observableArrayList();

    public void initialize() {
        // Initialize the checkbox list
        addressCheckBoxes.add(new CheckBox("Paris"));
        addressCheckBoxes.add(new CheckBox("New York"));
        addressCheckBoxes.add(new CheckBox("London"));

        // Set the filter button action handler
        FilterAnchor.setVisible(false);
        FilterButton.setOnAction(event -> {
            // Show/hide the filter anchor pane
            FilterAnchor.setVisible(!FilterAnchor.isVisible());
        });
    }

    @FXML
    private void filtrercinema(ActionEvent event) {
        cinemasList.setOpacity(1);

        // Rcuprer les adresses slectionnes
        List<String> selectedAddresses = getSelectedAddresses();

        // Filtrer les cinmas en fonction des adresses slectionnes
        List<Cinema> filteredCinemas = getAllCinemas().stream()
                .filter(cinema -> selectedAddresses.isEmpty() || selectedAddresses.contains(cinema.getAdresse()))
                .collect(Collectors.toList());

        // Mettre  jour le TableView avec les cinmas filtrs
        ObservableList<Cinema> filteredList = FXCollections.observableArrayList(filteredCinemas);
        listCinema.setItems(filteredList);
    }

    private List<String> getSelectedAddresses() {
        // Rcuprer les adresses slectionnes dans l'AnchorPane de filtrage
        return addressCheckBoxes.stream()
                .filter(CheckBox::isSelected)
                .map(CheckBox::getText)
                .collect(Collectors.toList());
    }
}

