import javafx.collections.FXCollections;
import javafx.scene.control.Label;
import javafx.scene.image.ImageView;
import javafx.scene.layout.AnchorPane;
import javafx.scene.layout.HBox;
import javafx.scene.text.Font;

public class Main extends Application {
    @Override
    public void start(Stage primaryStage) {
        HBox root = new HBox();

        // Create a cinema object and set its name, logo, and address
        Cinema cinema = new Cinema("cinema", "cinema.png", "123 Main St");

        // Use the createCinemaCard method to create a card for this cinema using the previous cinema object
        HBox cinemaCard = createCinemaCard(cinema);

        // Add the created cinema card to the root of the scene
        root.getChildren().add(cinemaCard);

        // Set the scene's user data to the current client
        primaryStage.setUserData(new Client("client"));

        // Create a label and set its text to the name of the current client
        Label clientLabel = new Label("Client: " + primaryStage.getUserData().getName());

        // Add the created label to the root of the scene
        root.getChildren().add(clientLabel);

        primaryStage.setScene(new Scene(root));
        primaryStage.show();
    }

    /**
     * Creates a cinema card using the given Cinema object.
     * @param cinema The Cinema object to create the card for.
     * @return A new HBox object containing the created cinema card.
     */
    public HBox createCinemaCard(Cinema cinema) {
        // Create an AnchorPane and set its style class to "card"
        AnchorPane anchorPane = new AnchorPane();
        anchorPane.getStyleClass().add("card");

        // Create an ImageView and set the image for it using the given cinema logo file name
        ImageView imageView = new ImageView(cinema.getLogo());

        // Create a label and set its text to the name of the given cinema object
        Label label = new Label(cinema.getName());

        // Set the font size for the created label using a value of 20
        label.setFont(new Font(20));

        // Add the created ImageView and label to the AnchorPane
        anchorPane.getChildren().addAll(imageView, label);

        return anchorPane;
    }
}

