private void handleTimeColumnEditing(TableView<Seance> tableView, TableColumn<Seance, Time> column) {
    // Create a custom cell factory for the time column
    Callback<TableColumn<Seance, Time>, TableCell<Seance, Time>> cellFactory = 
        (final TableColumn<Seance, Time> param) -> {
            final TableCell<Seance, Time> cell = new TableCell<Seance, Time>() {
                @Override
                public void commitEdit(Time newValue) {
                    super.commitEdit(newValue);

                    // Get the current row index and column index
                    int rowIndex = this.getTableRow().getIndex();
                    int colIndex = tableView.getColumns().indexOf(column);

                    Seance seance = tableView.getItems().get(rowIndex);
                    seance.setHF(newValue);

                    // Update the seance in the database
                    SeanceService seanceService = new SeanceService();
                    seanceService.update(seance);

                    // Set the text of the cell to the new value
                    setText(String.valueOf(newValue));
                }
            };

            return cell;
        };

    column.setCellFactory(cellFactory);
}

