public class CustomDateCell extends TableCell<Person, LocalDate> {
    @Override
    protected void updateItem(LocalDate date, boolean empty) {
        super.updateItem(date, empty);
        if (empty || date == null) {
            setText(null);
        } else {
            DatePicker datePicker = new DatePicker();
            LocalDateTime localDateTime = date.toLocalDateTime();
            String formattedDate = localDateTime.toString("yyyy-MM-dd");
            datePicker.setValue(formattedDate);

            setText(date.toString());
        }

        setOnMouseClicked(event -> {
            if (event.getClickCount() == 2) {
                DatePicker datePicker = new DatePicker();

                if (!isEmpty() && getItem() != null) {
                    datePicker.setValue(getItem().toLocalDate());
                }

                datePicker.setOnAction(e -> {
                    LocalDate selectedDate = datePicker.getValue();
                    if (selectedDate != null) {
                        Date newDate = Date.valueOf(selectedDate);
                        commitEdit(newDate);
                    }
                });

                StackPane root = new StackPane(datePicker);
                Stage stage = new Stage();
                stage.initModality(Modality.APPLICATION_MODAL);
                stage.setScene(new Scene(root));
                stage.show();
            }
        });
    }
}

