    searchActor(newValue);
});

// Searching the list of actors based on the text in the TextField
private void searchActor(String searchText) {
    filteredList.setPredicate(actor -> {
        // If the search bar is empty, show all Actors
        if (searchText == null || searchText.isEmpty()) {
            return true;
        }

        // Checking if the Actor's name contains the text in the TextField
        String lowerCaseFilter = searchText.toLowerCase();
        return actor.getNom().toLowerCase().contains(lowerCaseFilter);
    });
}

