    private void setupCellFactory() {
        // Set up the cell factory for each table column.
        Callback<TableColumn, TableCell> stringCellFactory = new Callback<TableColumn, TableCell>() {
            @Override
            public TableCell call(final TableColumn p) {
                return new TableCell<Category, String>() {
                    private final TextField textField = new TextField();
                    private final Tooltip tooltip = new Tooltip();

                    @Override
                    public void startEdit() {
                        if (!isEmpty()) {
                            super.startEdit();

                            // Set the cell value factory to a property so that we can get/set the data in the text field.
                            textField.textProperty().bindBidirectional(getTableView().getItems().get(getIndex()).getValue());

                            // Setup tooltip and cell formatting for error handling.
                            Validator<String> validator = new LengthValidator(3, 10);
                            textField.textProperty().addListener(new ChangeListener<String>() {
                                @Override
                                public void changed(ObservableValue<? extends String> observable, String oldValue, String newValue) {
                                    System.out.println(validator.containsErrors());
                                    if (validator.containsErrors()) {
                                        tooltip.setText(validator.createStringBinding().getValue());
                                        tooltip.setStyle("-fx-background-color: red;");
                        textField.focusedProperty().addListener((observable, oldValue, newValue) -> {
                            if (newValue) {
                                textField.selectAll();
                            }
                        });
                    } else {
                        tooltip.setText("");
                        tooltip.setStyle(null);
                        textField.focusedProperty().removeListener((observable, oldValue, newValue) -> {});
                    }
                }
            );

            // Setup the text field to be editable.
            setGraphic(textField);

            // Handle cell selection changes.
            selectedProperty().addListener((observable, oldValue, newValue) -> {
                        if (newValue) {
                            // When a user selects a cell we need to update the tooltip and text field.
                            Validator<String> validator = new LengthValidator(3, 10);
                            String value = getTableView().getItems().get(getIndex()).getValue();

                            if (validator.containsErrors(value)) {
                                tooltip.setText(validator.createStringBinding().getValue());
                                tooltip.setStyle("-fx-background-color: red;");
                                textField.focusedProperty().addListener((observable, oldValue, newValue) -> {
                                    if (newValue) {
                                        textField.selectAll();
                                    }
                                });
                            } else {
                                tooltip.setText("");
                                tooltip.setStyle(null);
                                textField.focusedProperty().removeListener((observable, oldValue, newValue) -> {});
                            }
                        }
                    }
            });

            // Handle keyboard and mouse input to the text field.
            setOnKeyPressed(event -> {
                    if (event.getCode() == KeyCode.ENTER) {
                        commitEdit(textField.getText());
                    } else if (event.getCode() == KeyCode.ESCAPE) {
                        cancelEdit();
                    }
            });

            setOnMouseClicked(mouseEvent -> {
                if (mouseEvent.getButton().equals(MouseButton.PRIMARY)) {
                    commitEdit(textField.getText());
                } else if (mouseEvent.getButton().equals(MouseButton.SECONDARY)) {
                    cancelEdit();
                }
            });
        };
    };

    private void setupCellValueFactory() {
        // Set the cell value factory for each table column.
        nomCategory_tableColumn.setCellValueFactory(new Callback<Integer, ObservableValue<String>>() {
            @Override
            public ObservableValue<String> call(final Integer index) {
                return new SimpleStringProperty("Name");
