        descrptionCategory_tableColumn.setCellValueFactory(new Callback<Integer, ObservableValue<String>>() {
            @Override
            public ObservableValue<String> call(final Integer index) {
                return new SimpleStringProperty("Description");
            }
        });
    };

    private void setupCellOnEditCommit() {
        // Setup cell formatting and error handling for the table columns.
        nomCategory_tableColumn.setCellFactory(stringCellFactory);
        descrptionCategory_tableColumn.setCellFactory(stringCellFactory);
    };

