import javafx.scene.layout.AnchorPane;
import javafx.scene.layout.VBox;
import javafx.stage.Stage;
import java.util.List;

public class Main extends Application {
    private FlowPane flowpaneFilm = new FlowPane();

    public static void main(String[] args) {
        launch(args);
    }

    @Override
    public void start(Stage stage) throws Exception {
        List<Film> films = FilmService.read(); // Read a list of films from the database
        createfilmCards(films); // Call createfilmCards method to create a card for each film in the list
        AnchorPane anchorPane = new AnchorPane();
        anchorPane.getChildren().add(flowpaneFilm);
        Scene scene = new Scene(anchorPane, 500, 400);
        stage.setScene(scene);
        stage.show();
    }

    private AnchorPane createFilmCard(Film film) {
        ImageView imageView = new ImageView(film.getImage()); // Create an image view to display the image of the film
        VBox vbox = new VBox();
        Text text = new Text(film.getName()); // Create a text label with the name of the film
        Button button = new Button("Lire la description"); // Create a button to read the description of the film
        button.setOnAction(e -> { // Set an action to perform when the button is clicked
            System.out.println(film.getDescription()); // Print out the description of the film
        });
        vbox.getChildren().addAll(imageView, text, button);
        return new AnchorPane(vbox);
    }

    private void createfilmCards(List<Film> Films) {
        for (Film film : Films) {
            AnchorPane cardContainer = createFilmCard(film); // Call createFilmCard method to create a card for each film in the list
            flowpaneFilm.getChildren().add(cardContainer); // Add each card to the flow pane
        }
    }
}

