    @Override
    public void start(Stage stage) throws Exception {
        // Create a new Filmcoment object and set its fields to appropriate values.
        Filmcoment comment = new Filmcoment("Filmcoment", "John Doe");
        // Call the addCommentToView method, passing in the 'comment' parameter with the value of the 'Filmcoment' object created above.
        HBox content = addCommentToView(comment);
        // Set the content for the ScrollPane to the HBox containing the comment.
        ScrollPaneComments.setContent(content);
    }
    private HBox addCommentToView(Filmcoment comment) {
        // Create a new Image object with the 'photo_de_profil' parameter as an image file path.
        Image userImage = new Image(comment.getPhoto_de_profil());
        // Create a new ImageView object, passing in the previously created Image object.
        ImageView userImageView = new ImageView(userImage);
        // Set the fit width and height of the image view.
        userImageView.setFitWidth(50);
        userImageView.setFitHeight(50);
        // Create a new HBox object with the previously created ImageView object as its content.
        HBox userImageHBox = new HBox();
        userImageHBox.getChildren().add(userImageView);
        // Create a new VBox object to contain the text for the comment.
        VBox textVBox = new VBox();
        // Create a new Text object with the 'comment' parameter as its string value.
        Text userCommentText = new Text(comment.getComment());
        // Add the previously created Text object to the VBox.
        textVBox.getChildren().add(userCommentText);
        // Set the style for the HBox containing the image, with padding of 50 pixels between elements and a radius of 10 pixels.
        userImageHBox.setStyle("-fx-padding: 50; -fx-border-radius: 10");
        // Add the VBox to the HBox.
        userImageHBox.getChildren().add(textVBox);
        return userImageHBox;
    }
}

