@Override
    public void onChanged(Change<? extends String> change) {
        while (change.next()) {
            if (change.wasAdded()) {
                // Get the Seance object from the observable list of seances using the index of the added item and set the seance field in the controller to this object
                int index = change.getList().indexOf(change.getValueAdded());
                Seance seance = seances.get(index);
                this.seance = seance;

                // Set all children of the anchorpane_payment AnchorPane to be disabled by iterating through each child and setting its disable property to true
                anchorpane_payment.getChildren().forEach(node -> node.setDisable(true));

                // Set the value factory for the nbrplacepPayment_Spinner to a new SpinnerValueFactory that generates values between 1 and the number of places in the Seance object set above, and sets the initial value of the spinner to be 1 using the setValue(int) method
                nbrplacepPayment_Spinner.setValueFactory(new SpinnerValueFactory.IntegerSpinnerValueFactory(1, seance.getId_salle().getNb_places(), 1, 1));
            }
        }
    }

