@FXML
private void payment(ActionEvent event) {
    // Set up PayPal API context
    APIContext apiContext = new APIContext(CLIENT_ID, CLIENT_SECRET, "sandbox");

    // Create amount object
    Amount amount = new Amount();
    amount.setCurrency("USD");
    amount.setTotal("19.99"); // This should be the total price of your order

    // Create transaction object
    Transaction transaction = new Transaction();
    transaction.setDescription("Your Purchase Description");
    transaction.setAmount(amount);

    // Add transaction to list
    List<Transaction> transactions = new ArrayList<>();
    transactions.add(transaction);

    // Create payer object
    Payer payer = new Payer();
    payer.setPaymentMethod("paypal");

    // Create payment object and set fields
    Payment payment = new Payment();
    payment.setIntent("sale");
    payment.setPayer(payer);
    payment.setTransactions(transactions);
    payment.setRedirectUrls(new RedirectUrls());

    // Try to create payment and redirect if successful
    try {
        Payment createdPayment = payment.create(apiContext);
        for (Links link : createdPayment.getLinks()) {
            if (link.getRel().equalsIgnoreCase("approval_url")) {
                System.out.println(link.getHref());
                redirectToPayPal(link.getHref());
                break;
            }
        }
    } catch (PayPalRESTException e) {
        e.printStackTrace();
        showAlert("Error", "Could not initiate PayPal payment.");
    }
}

